const Story = {
  "title" : "Aprendendo Adonis.js - reaproveitando messagens de validação"
}

'use strict'

const StandartMessages = require('./StandartMessages')

class RegisterUser {

  get rules () {
    return {
      email: 'required|unique:users,email',
      password: 'required|min:6|max:20'
    }
  }

  get sanitizationRules () {
    return {
      email: 'normalize_email'
    }
  }

  get messages() {
    return {
      "email.required" : "Insira um email válido",
      "password.required" : "Insira um password válido",
    }
  }

  async fails(errorMessages) {
    return this.ctx.response.json({ errors: errorMessages.map((error) => error.message) })
  }

}

module.exports = RegisterUser

