class Person{
  constructor(name, age) {
    this.name = name
    this.age = age
  }
}

class Developer extends Person {

  constructor(name, age) {
    super(name, age)
  }

  personalCodeLibrary = []

  learnSomethingNew(title, subject) {
    const newCode = new Code(title, subject)
    this.personalCodeLibrary.push(new Code(title, subject))
  }
}

const me = new Developer('Igor Trindade', '33')

me.learnSomethingNew('Javascript OOP', 'javascript')
